package AdventCode;

public class dayOne {
	
	static String str = "((()))())";
	static char left = '(';
	static char right = ')';
	
	public static int direction(String args){
		int floor = 0;
		
		for(int i = 0; i < args.length(); i++){
			if (args.charAt(i) == left){
				floor++;
			}
			if (args.charAt(i) == right){
				floor--;
			}
		}
		
		return floor;
	}
	
	public static void main(String[] args) {
		System.out.println(direction(str));
	}

}

